package javabasic.session2.belajar;

public class CobaLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=0; i<5; i=i+1){
			 //melakukan perulangan dari variabel int i yang dimulai dari angka 0
	           //dengan syarat atau kondisi i kurang dari 5 kali perulangan
	           // nilai i(+i) akan terus mengulang sampai syarat
	           // atau kondisi batas perulangan terpenuhi
			System.out.println(i);
		}
	}

}
